import React from 'react';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button'
import Card from 'react-bootstrap/Card';
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import styled from 'styled-components';


const Space = styled.div`
	.space {
		width: 10px
	}
`;


export default class Login extends React.Component {
	constructor(props){
		super(props);
		this.state = {
			email: '',
			password: ''
		}
	}


handleSubmit = (event) => {
	event.preventDefault();
	const data = this.state;
	console.log('Log in data is: ', data);

}

handleInputChange = (event) => {
	this.setState({
			[event.target.name]: event.target.value
	})

}


render() {
	const {email, password} = this.state;
	return(
		<div>
        <Card className="message-form" style={{ width: '16rem' }}>
          <Card.Body>
            <Card.Title>Welcome to Global-Port</Card.Title>
            <Card.Text>
              Connect with fellow travelers and explore the world!
            </Card.Text>
              <Form onSubmit={this.handleSubmit}>
                <Form.Group controlId="formBasicEmail">
                  <Form.Label>Email address</Form.Label>
                  <Form.Control
                    onChange={(e) => this.handleInputChange(e)} 
                    size="sm"
                    type="email" 
                    name="email"
                    placeholder="Enter email"
                   />
                  <Form.Text className="text-muted">
                    We'll never share your email with anyone else.
                    this is email: {email}
                  </Form.Text>
                </Form.Group>

                <Form.Group controlId="formBasicPassword">
                  <Form.Label>Password</Form.Label>
                  <Form.Control 
                    onChange={(e) => this.handleInputChange(e)}
                    size="sm"
                    type="password" 
                    name="password"
                    placeholder="Password" />
                  <Form.Text className="text-muted">
                    pass: {password}
                  </Form.Text>
                </Form.Group>

                <Container>
                	<Row>
	                	<Col> <Button type="submit" variant="info">Log in</Button></Col>
	    				<Col xs={6}> <Button type="submit" variant="info">Sign up</Button></Col>
                	</Row>
                </Container>

              </Form>

          </Card.Body>
        </Card>
		</div>
		);
	}
}
