	// 		  block
	// 		  type="submit"
	// 		  bsSize="large"
	// 		  isLoading={isLoading}
	// 		  disabled={!validateConfirmationForm()}
	// 		>
	// 		  Verify
	// 		</LoaderButton>
	// 	  </form>
	// 	);
	// }

	render() {
		const {name, email, confirmEmail, password, confirmPassword} = this.state;
		return(
			<Styles>
			<Form onSubmit={(e) => this.handleSubmit(e)} className="formCentered">

			<Form.Group controlId="formGroupName">
			    <Form.Label>Enter your name</Form.Label>
			    <Form.Control type="name" placeholder="Enter full name" 
			    name="name" onChange={(e) => this.handleInputChange(e)} />
			  </Form.Group>

			  <Form.Group controlId="formGroupEmail">
			    <Form.Label>Enter your email</Form.Label>
			    <Form.Control type="email" placeholder="Enter email" 
			    name="email" onChange={(e) => this.handleInputChange(e)} />
			  </Form.Group>

			  <Form.Group controlId="formGroupEmailConfirm">
			    <Form.Label>Confirm email</Form.Label>
			    <Form.Control type="confimEmail" placeholder="Confirm your email" 
			    name="comfirmEmail" onChange={(e) => this.handleInputChange(e)} />
			  </Form.Group>

			  <Form.Group controlId="formGroupPassword">
			    <Form.Label>Enter your password</Form.Label>
			    <Form.Control type="password" placeholder="Password" 
			    name="password" onChange={(e) => this.handleInputChange(e)}/>
			  </Form.Group>

			  <Form.Group controlId="formGroupPasswordConfirm">
			    <Form.Label>Confirm your password</Form.Label>
			    <Form.Control type="confirmPassword" placeholder="Confirm your password" 
			    name="confirmPassword" onChange={(e) => this.handleInputChange(e)}/>
			  </Form.Group>

			  <Button variant="primary" type="submit">
			  Log In</Button>
			</Form>
			</Styles>
		);
	}
}
