import React from 'react';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button'
import styled from 'styled-components';
import Card from 'react-bootstrap/Card';

const Styles = styled.div`

	.formCentered {
		margin: 0 auto;
		width: 40%
	}
`;


export default class Login extends React.Component {
    constructor(props) {
      super(props);
      this.state = {
        location: {
          lat: 51.505,
          lng: -0.09,
        },
        haveUsersLocation: false,
        zoom: 2,
        userMessage: {
          email: '',
          password: '',
          message: ''
        }
      }
    }


    formSubmitted = (event) => {
      event.preventDefault();
      console.log(this.state.userMessage);
    }

    valueChanged = (event) => {
      event.persist();
     // const { name, value } = event.target;
      this.setState((prevState) => ({
        userMessage: {
          ...prevState.userMessage,
          [event.target.name]: event.target.value
        }
      }))
    }


	render() {
		const {email, password} = this.state;
		return(
		<div>
		<Card className="message-form">
          <Card.Body>
            <Card.Title>Welcome to Global-Port</Card.Title>
            <Card.Text>
              Connect with fellow travelers and explore the world!
            </Card.Text>
              <Form onSubmit={this.formSubmitted}>
                <Form.Group controlId="formBasicEmail">
                  <Form.Label>Email address</Form.Label>
                  <Form.Control
                    onChange={(e) => this.valueChanged(e)} 
                    size="sm"
                    type="email" 
                    name="email"
                    placeholder="Enter email"
                   />
                  <Form.Text className="text-muted">
                    We'll never share your email with anyone else.
                    this is email: {email}
                  </Form.Text>
                </Form.Group>

                <Form.Group controlId="formBasicPassword">
                  <Form.Label>Password</Form.Label>
                  <Form.Control 
                    onChange={(e) => this.valueChanged(e)}
                    size="sm"
                    type="password" 
                    name="password"
                    placeholder="Password" />
                  <Form.Text className="text-muted">
                    pass: {password}
                  </Form.Text>
                </Form.Group>

                <Form.Group controlId="exampleForm.ControlTextarea1">
                  <Form.Label>Message</Form.Label>
                  <Form.Control 
                    onChange={(e) => this.valueChanged(e)}
                    size="sm"
                    type="textarea"
                    name="message"
                    as="textarea" 
                    rows="3" />
                  <Form.Text className="text-muted">
                    mess: {message}
                  </Form.Text>
                </Form.Group>

                <Button type="submit" variant="info" disabled={!this.state.haveUsersLocation}>Log in</Button>

              </Form>

          </Card.Body>
        </Card>
      </div>
		);
	}
}
