import React from 'react';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button'
import Card from 'react-bootstrap/Card';
import styled from 'styled-components';

const Styles = styled.div`
	.formCentered {
		margin: 0 auto;
		width: 40%
	}
`;


export default class Login extends React.Component {
	constructor(props){
		super(props);
		this.state = {
			email: '',
			password: ''
		}
	}


handleSubmit = (event) => {
	event.preventDefault();
	const data = this.state;
	console.log('Log in data is: ', data);

}

handleInputChange = (event) => {
	this.setState({
			[event.target.name]: event.target.value
	})

}


render() {
	const {email, password} = this.state;
	return(
		<Styles>
        <Card className="message-form">
          <Card.Body>
            <Card.Title>Welcome to Global-Port</Card.Title>
            <Card.Text>
              Connect with fellow travelers and explore the world!
            </Card.Text>
              <Form onSubmit={this.handleSubmit}>
                <Form.Group controlId="formBasicEmail">
                  <Form.Label>Email address</Form.Label>
                  <Form.Control
                    onChange={(e) => this.handleInputChange(e)} 
                    size="sm"
                    type="email" 
                    name="email"
                    placeholder="Enter email"
                   />
                  <Form.Text className="text-muted">
                    We'll never share your email with anyone else.
                    this is email: {email}
                  </Form.Text>
                </Form.Group>

                <Form.Group controlId="formBasicPassword">
                  <Form.Label>Password</Form.Label>
                  <Form.Control 
                    onChange={(e) => this.handleInputChange(e)}
                    size="sm"
                    type="password" 
                    name="password"
                    placeholder="Password" />
                  <Form.Text className="text-muted">
                    pass: {password}
                  </Form.Text>
                </Form.Group>

                <Button type="submit" variant="info" /*disabled={!this.state.haveUsersLocation}*/>Log in</Button>

              </Form>

          </Card.Body>
        </Card>
		</Styles>
		);
	}
}
